function Tania {
    <#
    .SYNOPSIS
        Print a professional Investor / Creditor profile for Tania (Ommu Faija) using Write-Host.

    .DESCRIPTION
        Displays a clean, aligned profile header (name, role, contact, email/address optional),
        then prints a transaction table and totals. All output uses Write-Host so it is easy to copy into logs.
    #>

    param(
        [Parameter(Mandatory=$false)]
        [array]$Transactions = @(
            # Example transaction objects (Date, Amount, Method, Mobile, Trx)
            # [PSCustomObject]@{ Date="2026-02-01 14:30"; Amount=5000; Method="Bkash"; Mobile="+880-1862-193199"; Trx="TRX123ABC" }
        )
    )

    # Profile
    $name    = "Tania (Ommu Faija)"
    $role    = "Investor"
    $phone   = "+880-1862-193199"
    $email   = ""    # add if available
    $address = ""    # add if available

    # Header
    Write-Host ""
    Write-Host "================================================================================" -ForegroundColor Cyan
    Write-Host (" Name    : {0}" -f $name) -ForegroundColor Yellow
    if ($address) { Write-Host (" Address : {0}" -f $address) }
    if ($email)   { Write-Host (" Email   : {0}" -f $email) }
    Write-Host (" Phone   : {0}" -f $phone)
    Write-Host (" Role    : {0}" -f $role)
    Write-Host "================================================================================"
    Write-Host ""

    # Transactions header (aligned)
    Write-Host " Date and Time        | Amount (Tk) | Method | Mobile               | Transaction ID"
    Write-Host " -------------------------------------------------------------------------------"

    if (-not $Transactions -or $Transactions.Count -eq 0) {
        Write-Host " No transactions recorded." -ForegroundColor Yellow
    } else {
        foreach ($t in $Transactions) {
            $date = if ($t.Date) { $t.Date } else { "-" }
            $amt  = if ($t.Amount) { "{0:N0}" -f $t.Amount } else { "-" }
            $meth = if ($t.Method) { $t.Method } else { "-" }
            $mob  = if ($t.Mobile) { $t.Mobile } else { "-" }
            $trx  = if ($t.Trx) { $t.Trx } else { "-" }

            $line = "{0,-20} | {1,11} | {2,-6} | {3,-19} | {4,-15}" -f $date, $amt, $meth, $mob, $trx
            Write-Host " " + $line
        }

        Write-Host " -------------------------------------------------------------------------------"

        $totalAmount = ($Transactions | Measure-Object -Property Amount -Sum).Sum
        $bkashTotal  = ($Transactions | Where-Object { $_.Method -ieq "Bkash" } | Measure-Object -Property Amount -Sum).Sum
        $cashTotal   = ($Transactions | Where-Object { $_.Method -ieq "Cash" } | Measure-Object -Property Amount -Sum).Sum

