function Get-MjDevContacts {
    param(
        [string]$Path = ".\contacts.json",
        [string]$Type,
        [string]$Name
    )

    # Load JSON file
    $contacts = Get-Content $Path | ConvertFrom-Json

    # Apply filters if provided
    if ($Type) {
        $contacts = $contacts | Where-Object { $_.type -eq $Type }
    }
    if ($Name) {
        $contacts = $contacts | Where-Object { $_.name -like "*$Name*" }
    }

    # Display in a visually balanced table
    $contacts | Select-Object `
        id,
        name,
        phone,
        whatsapp,
        email,
        organization,
        type,
        @{Name="Transactions";Expression={ ($_.transactions | ForEach-Object { "$($_.date): $($_.amount) $($_.currency)" }) -join "; " }},
        legacy_notice,
        localized_explanation_bn |
    Format-Table -AutoSize
}

