function Get-Video {
    <#
    .SYNOPSIS
        Downloads videos from multiple platforms (YouTube, Facebook, Instagram Reels, etc.)
    .DESCRIPTION
        Prompts the user for a video URL and output location, then downloads the video using yt-dlp.
    .NOTES
        Requires yt-dlp installed and available in PATH.
        Install via: pip install yt-dlp
    #>

    param()

    # Prompt for video URL
    $videoUrl = Read-Host "Enter the video URL"

    if ([string]::IsNullOrWhiteSpace($videoUrl)) {
        Write-Host "No URL provided. Exiting..." -ForegroundColor Red
        return
    }

    # Prompt for output location
    $outputPath = Read-Host "Enter the output folder path"

    if (-not (Test-Path $outputPath)) {
        Write-Host "Output path does not exist. Creating..." -ForegroundColor Yellow
        New-Item -ItemType Directory -Force -Path $outputPath | Out-Null
    }

    # Build yt-dlp command
    $command = "yt-dlp `"$videoUrl`" -o `"$outputPath\%(title)s.%(ext)s`""

    Write-Host "Downloading video..." -ForegroundColor Cyan
    Invoke-Expression $command

    Write-Host "Download complete! Saved to $outputPath" -ForegroundColor Green
}

