Get-NetIPAddress @"
# Get Local IPs
$localIPs = Get-NetIPAddress |
    Where-Object { $_.AddressFamily -in ('IPv4','IPv6') -and $_.IPAddress -notlike '169.*' } |
    Select-Object InterfaceAlias, IPAddress

# Get Public IP
$publicIP = Invoke-RestMethod -Uri "https://api.ipify.org?format=json"

# Get Public IP Geolocation (country check)
$geoInfo = Invoke-RestMethod -Uri "https://ipinfo.io/$($publicIP.ip)/json"

Write-Host "=== Local IPs (LAN + Wi-Fi) ===" -ForegroundColor Cyan
$localIPs | Format-Table -AutoSize

Write-Host "`nPublic IP: $($publicIP.ip)" -ForegroundColor Green
Write-Host "Location: $($geoInfo.country) - $($geoInfo.city)" -ForegroundColor Yellow

"@
Get-NetIPAddress1 @"
# Get IP, LAN, and Wi-Fi addresses
Write-Host "=== Network Information ===" -ForegroundColor Cyan

# Local IPv4 and IPv6 addresses
Get-NetIPAddress |
    Where-Object { $_.AddressFamily -in ('IPv4','IPv6') -and $_.IPAddress -notlike '169.*' } |
    Select-Object InterfaceAlias, IPAddress, PrefixLength

# LAN (Ethernet) adapter details
Get-NetAdapter |
    Where-Object { $_.InterfaceDescription -match "Ethernet" } |
    Select-Object Name, InterfaceDescription, MacAddress, Status, LinkSpeed

# Wi-Fi adapter details
Get-NetAdapter |
    Where-Object { $_.InterfaceDescription -match "Wi-Fi" } |
    Select-Object Name, InterfaceDescription, MacAddress, Status, LinkSpeed

# Public IP (via external service)
try {
    $publicIP = Invoke-RestMethod -Uri "https://api.ipify.org?format=json"
    Write-Host "Public IP Address: $($publicIP.ip)" -ForegroundColor Green
} catch {
    Write-Host "Unable to fetch public IP." -ForegroundColor Red
}

