const fs = require('fs');

function readFile(filePath) {
  return fs.readFileSync(filePath, 'utf-8');
}

function writeFile(filePath, data) {
  fs.writeFileSync(filePath, data, 'utf-8');
}

module.exports = { readFile, writeFile };

files.markdown_to_json_converter.js:
const { readFile, writeFile } = require('../utils/file_utils');
const { marked } = require('marked');

const input = readFile('example.md');
const tokens = marked.lexer(input);

writeFile('output.json', JSON.stringify(tokens, null, 2));
console.log('Markdown converted to JSON → output.json');

files.html_to_json_converter.js:
const { readFile, writeFile } = require('../utils/file_utils');
const { JSDOM } = require('jsdom');

const input = readFile('example.html');
const dom = new JSDOM(input);

const json = {
  title: dom.window.document.title,
  body: dom.window.document.body.innerHTML
};

writeFile('output.json', JSON.stringify(json, null, 2));
console.log('HTML converted to JSON → output.json');

files.json_to_markdown_converter.js:
const { readFile, writeFile } = require('../utils/file_utils');

const input = JSON.parse(readFile('output.json'));

let markdown = '';
input.forEach(token => {
  if (token.type === 'heading') markdown += `# ${token.text}\n\n`;
  else if (token.type === 'paragraph') markdown += `${token.text}\n\n`;
});

writeFile('output.md', markdown);
console.log('JSON converted to Markdown → output.md');

files.json_to_html_converter.js:
const { readFile, writeFile } = require('../utils/file_utils');

const input = JSON.parse(readFile('output.json'));

let html = '<html><body>';
if (input.title) html += `<h1>${input.title}</h1>`;
if (input.body) html += `<div>${input.body}</div>`;
html += '</body></html>';

writeFile('output.html', html);
console.log('JSON converted to HTML → output.html');

